import React from 'react';
import './Header.css';

interface HeaderProps {
  title: string;
  onSearch?: (query: string) => void;
  onFilter?: () => void;
  user?: {
    name: string;
    company: string;
  };
}

const Header: React.FC<HeaderProps> = ({ 
  title, 
  onSearch, 
  onFilter, 
  user 
}) => {
  return (
    <header className="header">
      <div className="header-left">
        <div className="logo">
          <div className="logo-icon">+</div>
          <span className="logo-text">LIFYESCALAS</span>
        </div>
      </div>

      <div className="header-center">
        <span className="header-title">{title}</span>
      </div>

      <div className="header-right">
        {onSearch && (
          <div className="search-container">
            <input
              type="text"
              placeholder="Pesquisar por setor..."
              className="search-input"
              onChange={(e) => onSearch(e.target.value)}
            />
          </div>
        )}

        <div className="header-actions">
          <button className="action-btn" title="Ajuda">
            <span>?</span>
          </button>
          <button className="action-btn" title="Mensagens">
            <span>‚úâ</span>
          </button>
          <button className="action-btn" title="Notifica√ß√µes">
            <span>üîî</span>
          </button>
          <button className="action-btn" title="Adicionar">
            <span>+</span>
          </button>
          <button className="action-btn" title="Perfil">
            <span>üë§</span>
          </button>
        </div>

        {user && (
          <div className="user-info">
            <span className="company-name">{user.company}</span>
            <select className="location-select">
              <option>Hospital 1¬∞ de Maio</option>
