import React from 'react';
import './Sidebar.css';

interface SidebarProps {
  activeItem: string;
  onItemClick: (item: string) => void;
}

const Sidebar: React.FC<SidebarProps> = ({ activeItem, onItemClick }) => {
  const menuItems = [
    { id: 'dashboard', label: 'Dashboard', icon: 'üìä' },
    { id: 'schedule', label: 'Escala', icon: 'üìÖ', subItems: [
      { id: 'schedule-weekly', label: 'Semanal' },
      { id: 'schedule-monthly', label: 'Mensal' }
    ]},
    { id: 'professional', label: 'Profissional', icon: 'üë•' },
    { id: 'search', label: 'Busca', icon: 'üîç' },
    { id: 'model', label: 'Modelo', icon: 'üìã' },
    { id: 'settings', label: 'Configura√ß√µes', icon: '‚öôÔ∏è', subItems: [
      { id: 'company', label: 'Empresa' },
      { id: 'location', label: 'Local' },
      { id: 'professional-register', label: 'Profissional' }
    ]}
  ];

  return (
    <aside className="sidebar">
      <div className="sidebar-content">
        {menuItems.map((item) => (
          <div key={item.id} className="menu-section">
            <button
              className={`menu-item ${activeItem === item.id ? 'active' : ''}`}
              onClick={() => onItemClick(item.id)}
            >
              <span className="menu-icon">{item.icon}</span>
              <span className="menu-label">{item.label}</span>
            </button>

            {item.subItems && (
              <div className="sub-menu">
                {item.subItems.map((subItem) => (
                  <button
                    key={subItem.id}
                    className={`sub-menu-item ${activeItem === subItem.id ? 'active' : ''}`}
                    onClick={() => onItemClick(subItem.id)}
                  >
                    <span className="sub-menu-icon">üìÖ</span>
                    <span className="sub-menu-label">{subItem.label}</span>
                  </button>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
    </aside>
  );
};

export default Sidebar;
